import { useQuery } from "@tanstack/react-query";
import { useState, useEffect } from "react";
import { AdminSidebar } from "@/components/AdminSidebar";
import { NewMemberModal } from "@/components/modals/NewMemberModal";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Plus, Search, User } from "lucide-react";
import { format } from "date-fns";
import { formatCurrency } from "@/utils/currency";
import { useAuth } from "@/hooks/useAuth";

export default function Members() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedGroupId, setSelectedGroupId] = useState<string>("all");
  const [isNewMemberModalOpen, setIsNewMemberModalOpen] = useState(false);
  const [memberSession, setMemberSession] = useState<any>(null);
  const { user } = useAuth();

  // Load member session for group context
  useEffect(() => {
    const sessionData = localStorage.getItem('memberSession');
    if (sessionData) {
      const session = JSON.parse(sessionData);
      setMemberSession(session);
    }
  }, []);

  // Determine if this is a group leader or admin
  const isAdmin = !!user;
  const isGroupLeader = memberSession && ['chairman', 'secretary', 'finance'].includes(memberSession.member?.groupRole);
  const userGroupId = memberSession?.member?.groupId;

  // For group leaders, fetch only their group members; for admins, fetch all members
  const { data: members = [], isLoading: membersLoading } = useQuery({
    queryKey: isGroupLeader ? ["/api/groups", userGroupId, "members"] : ["/api/members"],
    queryFn: async () => {
      if (isGroupLeader && userGroupId) {
        const response = await fetch(`/api/groups/${userGroupId}/members`);
        if (!response.ok) throw new Error('Failed to fetch group members');
        return response.json();
      } else {
        // For admins or fallback
        const response = await fetch('/api/members');
        if (!response.ok) throw new Error('Failed to fetch members');
        return response.json();
      }
    },
    enabled: isAdmin || (isGroupLeader && !!userGroupId),
  });

  const { data: groups = [], isLoading: groupsLoading } = useQuery({
    queryKey: ["/api/groups"],
    enabled: !isGroupLeader, // Only fetch groups for admins
  });

  const getGroupName = (groupId: string) => {
    const group = groups.find((g: any) => g.id === groupId);
    return group ? group.name : "Unknown Group";
  };

  const filteredMembers = members.filter((member: any) => {
    const matchesSearch = 
      `${member.firstName} ${member.lastName}`.toLowerCase().includes(searchTerm.toLowerCase()) ||
      member.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      member.phone?.includes(searchTerm);

    // For group leaders, don't filter by group (already filtered by API)
    // For admins, filter by selected group
    const matchesGroup = isGroupLeader || selectedGroupId === "all" || member.groupId === selectedGroupId;

    return matchesSearch && matchesGroup;
  });

  if (membersLoading || (!isGroupLeader && groupsLoading)) {
    return (
      <div className="min-h-screen flex">
        <AdminSidebar userRole={memberSession?.member?.groupRole || 'admin'} />
        <main className="flex-1 p-6">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-muted rounded w-1/3"></div>
            <div className="space-y-4">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-16 bg-muted rounded"></div>
              ))}
            </div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex" data-testid="members-page">
      <AdminSidebar userRole={memberSession?.member?.groupRole || 'admin'} />

      <main className="flex-1 overflow-hidden">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-foreground" data-testid="page-title">
                {isGroupLeader ? 'Group Members' : 'All Members'}
              </h2>
              <p className="text-muted-foreground">
                {isGroupLeader ? 'Manage members in your group' : 'Manage all group members'}
              </p>
            </div>
            <Button onClick={() => setIsNewMemberModalOpen(true)} data-testid="button-add-member">
              <Plus className="w-4 h-4 mr-2" />
              Add Member
            </Button>
          </div>
        </header>

        {/* Content */}
        <div className="p-6 overflow-y-auto h-[calc(100vh-88px)]">
          {/* Filters */}
          <div className="flex items-center gap-4 mb-6">
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Search members..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-9"
                data-testid="input-search-members"
              />
            </div>
            {/* Only show group filter for admins */}
            {!isGroupLeader && (
              <Select value={selectedGroupId} onValueChange={setSelectedGroupId}>
                <SelectTrigger className="w-48" data-testid="select-group-filter">
                  <SelectValue placeholder="Filter by group" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Groups</SelectItem>
                  {(groups as any[]).map((group: any) => (
                    <SelectItem key={group.id} value={group.id}>
                      {group.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            )}
          </div>

          {/* Members Table */}
          {filteredMembers.length === 0 ? (
            <div className="text-center py-12">
              <div className="w-24 h-24 mx-auto bg-muted rounded-full flex items-center justify-center mb-4">
                <User className="w-12 h-12 text-muted-foreground" />
              </div>
              <h3 className="text-lg font-medium text-foreground mb-2">No members found</h3>
              <p className="text-muted-foreground mb-4">
                {searchTerm || selectedGroupId !== "all" 
                  ? "Try adjusting your filters" 
                  : "Add your first member to get started"
                }
              </p>
              {!searchTerm && selectedGroupId === "all" && (
                <Button data-testid="button-add-first-member">
                  <Plus className="w-4 h-4 mr-2" />
                  Add First Member
                </Button>
              )}
            </div>
          ) : (
            <div className="bg-card rounded-lg border border-border overflow-hidden">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-muted/50">
                    <tr>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Name</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Contact</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Group</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Role</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Join Date</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Savings Balance</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Status</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredMembers.map((member: any) => (
                      <tr 
                        key={member.id} 
                        className="border-b border-border hover:bg-muted/25"
                        data-testid={`member-row-${member.id}`}
                      >
                        <td className="p-4">
                          <div className="flex items-center space-x-3">
                            <div className="w-8 h-8 bg-secondary rounded-full flex items-center justify-center">
                              <span className="text-xs font-medium text-secondary-foreground">
                                {member.firstName[0]}{member.lastName[0]}
                              </span>
                            </div>
                            <div>
                              <p className="font-medium text-foreground" data-testid={`member-name-${member.id}`}>
                                {member.firstName} {member.lastName}
                              </p>
                            </div>
                          </div>
                        </td>
                        <td className="p-4">
                          <div className="text-sm">
                            {member.email && (
                              <p className="text-foreground">{member.email}</p>
                            )}
                            {member.phone && (
                              <p className="text-muted-foreground">{member.phone}</p>
                            )}
                          </div>
                        </td>
                        <td className="p-4 text-sm text-foreground" data-testid={`member-group-${member.id}`}>
                          {getGroupName(member.groupId)}
                        </td>
                        <td className="p-4">
                          <Badge 
                            variant={
                              member.groupRole === 'chairman' ? 'default' :
                              member.groupRole === 'finance' ? 'secondary' :
                              member.groupRole === 'secretary' ? 'outline' : 
                              'destructive'
                            }
                          >
                            {member.groupRole?.charAt(0).toUpperCase() + member.groupRole?.slice(1) || 'Member'}
                          </Badge>
                        </td>
                        <td className="p-4 text-sm text-foreground">
                          {format(new Date(member.joinDate), "MMM dd, yyyy")}
                        </td>
                        <td className="p-4 text-sm font-medium text-foreground" data-testid={`member-balance-${member.id}`}>
                          {formatCurrency(parseFloat(member.savingsBalance || 0))}
                        </td>
                        <td className="p-4">
                          <span 
                            className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                              member.isActive 
                                ? 'bg-green-100 text-green-800' 
                                : 'bg-gray-100 text-gray-800'
                            }`}
                          >
                            {member.isActive ? 'Active' : 'Inactive'}
                          </span>
                        </td>
                        <td className="p-4">
                          <Button variant="ghost" size="sm" data-testid={`button-view-member-${member.id}`}>
                            View
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      </main>

      {/* New Member Modal */}
      {isGroupLeader && memberSession?.member ? (
        <NewMemberModal 
          open={isNewMemberModalOpen} 
          onOpenChange={setIsNewMemberModalOpen}
          groupId={memberSession.member.groupId}
          groupName={memberSession.member.groupName}
        />
      ) : isAdmin && selectedGroupId !== "all" ? (
        <NewMemberModal 
          open={isNewMemberModalOpen} 
          onOpenChange={setIsNewMemberModalOpen}
          groupId={selectedGroupId}
          groupName={groups?.find((g: any) => g.id === selectedGroupId)?.name || "Selected Group"}
        />
      ) : null}
    </div>
  );
}

client/src/pages/Transactions.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import { AdminSidebar } from "@/components/AdminSidebar";
import { TransactionRow } from "@/components/TransactionRow";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Plus, DollarSign } from "lucide-react";

export default function Transactions() {
  const [selectedGroupId, setSelectedGroupId] = useState<string>("all");
  const [selectedType, setSelectedType] = useState<string>("all");

  const { data: transactions = [], isLoading: transactionsLoading } = useQuery({
    queryKey: ["/api/transactions"],
  });

  const { data: groups = [] } = useQuery({
    queryKey: ["/api/groups"],
  });

  const { data: members = [] } = useQuery({
    queryKey: ["/api/members"],
  });

  const getMemberName = (memberId: string) => {
    const member = members.find((m: any) => m.id === memberId);
    return member ? `${member.firstName} ${member.lastName}` : "Unknown Member";
  };

  const getGroupName = (groupId: string) => {
    const group = groups.find((g: any) => g.id === groupId);
    return group ? group.name : "Unknown Group";
  };

  const filteredTransactions = transactions.filter((transaction: any) => {
    const matchesGroup = selectedGroupId === "all" || transaction.groupId === selectedGroupId;
    const matchesType = selectedType === "all" || transaction.type === selectedType;
    return matchesGroup && matchesType;
  });

  if (transactionsLoading) {
    return (
      <div className="min-h-screen flex">
        <AdminSidebar />
        <main className="flex-1 p-6">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-muted rounded w-1/3"></div>
            <div className="space-y-4">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="h-16 bg-muted rounded"></div>
              ))}
            </div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex" data-testid="transactions-page">
      <AdminSidebar />

      <main className="flex-1 overflow-hidden">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-foreground" data-testid="page-title">Transactions</h2>
              <p className="text-muted-foreground">View and manage all financial transactions</p>
            </div>
            <Button data-testid="button-record-transaction">
              <Plus className="w-4 h-4 mr-2" />
              Record Transaction
            </Button>
          </div>
        </header>

        {/* Content */}
        <div className="p-6 overflow-y-auto h-[calc(100vh-88px)]">
          {/* Filters */}
          <div className="flex items-center gap-4 mb-6">
            <Select value={selectedGroupId} onValueChange={setSelectedGroupId}>
              <SelectTrigger className="w-48" data-testid="select-group-filter">
                <SelectValue placeholder="Filter by group" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Groups</SelectItem>
                {groups.map((group: any) => (
                  <SelectItem key={group.id} value={group.id}>
                    {group.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select value={selectedType} onValueChange={setSelectedType}>
              <SelectTrigger className="w-48" data-testid="select-type-filter">
                <SelectValue placeholder="Filter by type" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Types</SelectItem>
                <SelectItem value="deposit">Deposit</SelectItem>
                <SelectItem value="withdrawal">Withdrawal</SelectItem>
                <SelectItem value="loan_payment">Loan Payment</SelectItem>
                <SelectItem value="loan_disbursement">Loan Disbursement</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Transactions Table */}
          {filteredTransactions.length === 0 ? (
            <div className="text-center py-12">
              <div className="w-24 h-24 mx-auto bg-muted rounded-full flex items-center justify-center mb-4">
                <DollarSign className="w-12 h-12 text-muted-foreground" />
              </div>
              <h3 className="text-lg font-medium text-foreground mb-2">No transactions found</h3>
              <p className="text-muted-foreground mb-4">
                {selectedGroupId !== "all" || selectedType !== "all" 
                  ? "Try adjusting your filters" 
                  : "Record your first transaction to get started"
                }
              </p>
              {selectedGroupId === "all" && selectedType === "all" && (
                <Button data-testid="button-record-first-transaction">
                  <Plus className="w-4 h-4 mr-2" />
                  Record First Transaction
                </Button>
              )}
            </div>
          ) : (
            <div className="bg-card rounded-lg border border-border overflow-hidden">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-muted/50">
                    <tr>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Date</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Member</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Type</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Amount</th>
                      <th className="text-left p-4 text-sm font-medium text-muted-foreground">Group</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredTransactions.map((transaction: any) => (
                      <TransactionRow
                        key={transaction.id}
                        transaction={transaction}
                        memberName={getMemberName(transaction.memberId)}
                        groupName={getGroupName(transaction.groupId)}
                      />
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

client/src/pages/UserManagement.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { AdminSidebar } from "@/components/AdminSidebar";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Plus, Users, UserCheck, UserX, QrCode } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";

const createUserSchema = z.object({
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  phone: z.string().min(10, "Phone number must be at least 10 digits"),
  email: z.string().email().optional().or(z.literal("")),
  role: z.enum(["admin", "field_monitor", "field_attendant"]),
  location: z.string().optional(),
  userId: z.string().optional(), // Optional, will be auto-generated if not provided
  pin: z.string().length(6, "PIN must be exactly 6 digits"),
});

type CreateUserData = z.infer<typeof createUserSchema>;

export default function UserManagement() {
  const [activeTab, setActiveTab] = useState("users");
  const { toast } = useToast();

  const { register, handleSubmit, reset, watch, setValue, formState: { errors } } = useForm<CreateUserData>({
    resolver: zodResolver(createUserSchema),
    defaultValues: {
      role: "field_attendant",
      pin: "",
    },
  });

  const { data: users = [] } = useQuery({
    queryKey: ["/api/users"],
    retry: false,
  });

  const createUserMutation = useMutation({
    mutationFn: async (userData: CreateUserData) => {
      const response = await apiRequest("POST", "/api/users", userData);
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      reset();
      toast({
        title: "Success",
        description: "User created successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const generateRandomUserId = () => {
    const randomNum = Math.floor(100000 + Math.random() * 900000);
    return `TD${randomNum}`;
  };

  const generateUserId = () => {
    const newUserId = generateRandomUserId();
    setValue("userId", newUserId);
  };

  const generateRandomPin = () => {
    const randomPin = Math.floor(100000 + Math.random() * 900000).toString();
    setValue("pin", randomPin);
  };

  const onSubmit = (data: CreateUserData) => {
    // Generate User ID if not provided
    if (!data.userId) {
      data.userId = generateRandomUserId();
    }
    createUserMutation.mutate(data);
  };

  const getRoleBadgeColor = (role: string) => {
    switch (role) {
      case "admin":
        return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200";
      case "field_monitor":
        return "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200";
      case "field_attendant":
        return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200";
      default:
        return "bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200";
    }
  };

  return (
    <div className="flex h-screen bg-background">
      <AdminSidebar />

      <main className="flex-1 overflow-auto">
        <div className="p-8">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-foreground mb-2">User Management</h1>
            <p className="text-muted-foreground">Manage WEKA system users and their roles</p>
          </div>

          <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
            <TabsList>
              <TabsTrigger value="users" className="flex items-center space-x-2">
                <Users className="w-4 h-4" />
                <span>All Users</span>
              </TabsTrigger>
              <TabsTrigger value="create" className="flex items-center space-x-2">
                <Plus className="w-4 h-4" />
                <span>Create User</span>
              </TabsTrigger>
            </TabsList>

            <TabsContent value="users" className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Users className="w-5 h-5" />
                    <span>System Users</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>User ID</TableHead>
                        <TableHead>Name</TableHead>
                        <TableHead>Phone</TableHead>
                        <TableHead>Role</TableHead>
                        <TableHead>Location</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>Actions</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {users.map((user: any) => (
                        <TableRow key={user.id}>
                          <TableCell className="font-mono">{user.userId}</TableCell>
                          <TableCell>{`${user.firstName} ${user.lastName}`}</TableCell>
                          <TableCell>{user.phone}</TableCell>
                          <TableCell>
                            <Badge className={getRoleBadgeColor(user.role)}>
                              {user.role.replace("_", " ").toUpperCase()}
                            </Badge>
                          </TableCell>
                          <TableCell>{user.location || "â€”"}</TableCell>
                          <TableCell>
                            {user.isActive ? (
                              <Badge className="bg-green-100 text-green-800">
                                <UserCheck className="w-3 h-3 mr-1" />
                                Active
                              </Badge>
                            ) : (
                              <Badge className="bg-red-100 text-red-800">
                                <UserX className="w-3 h-3 mr-1" />
                                Inactive
                              </Badge>
                            )}
                          </TableCell>
                          <TableCell>
                            <Button size="sm" variant="outline">
                              <QrCode className="w-4 h-4 mr-1" />
                              Barcode
                            </Button>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="create" className="space-y-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Plus className="w-5 h-5" />
                    <span>Create New User</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div className="space-y-2">
                        <Label htmlFor="firstName">First Name</Label>
                        <Input
                          id="firstName"
                          {...register("firstName")}
                          data-testid="input-first-name"
                        />
                        {errors.firstName && (
                          <p className="text-sm text-red-600">{errors.firstName.message}</p>
                        )}
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="lastName">Last Name</Label>
                        <Input
                          id="lastName"
                          {...register("lastName")}
                          data-testid="input-last-name"
                        />
                        {errors.lastName && (
                          <p className="text-sm text-red-600">{errors.lastName.message}</p>
                        )}
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="phone">Phone Number</Label>
                        <Input
                          id="phone"
                          type="tel"
                          {...register("phone")}
                          data-testid="input-phone"
                        />
                        {errors.phone && (
                          <p className="text-sm text-red-600">{errors.phone.message}</p>
                        )}
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="email">Email (Optional)</Label>
                        <Input
                          id="email"
                          type="email"
                          {...register("email")}
                          data-testid="input-email"
                        />
                        {errors.email && (
                          <p className="text-sm text-red-600">{errors.email.message}</p>
                        )}
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="role">Role</Label>
                        <Select onValueChange={(value) => setValue("role", value as any)}>
                          <SelectTrigger data-testid="select-role">
                            <SelectValue placeholder="Select role" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="admin">Admin</SelectItem>
                            <SelectItem value="field_monitor">Field Monitor</SelectItem>
                            <SelectItem value="field_attendant">Field Attendant</SelectItem>
                          </SelectContent>
                        </Select>
                        {errors.role && (
                          <p className="text-sm text-red-600">{errors.role.message}</p>
                        )}
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="location">Location (Optional)</Label>
                        <Input
                          id="location"
                          {...register("location")}
                          data-testid="input-location"
                        />
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="userId">User ID</Label>
                        <div className="flex space-x-2">
                          <Input
                            id="userId"
                            {...register("userId")}
                            placeholder="Auto-generated if empty"
                            data-testid="input-user-id"
                          />
                          <Button
                            type="button"
                            variant="outline"
                            onClick={generateUserId}
                            data-testid="button-generate-user-id"
                          >
                            Generate
                          </Button>
                        </div>
                        {errors.userId && (
                          <p className="text-sm text-red-600">{errors.userId.message}</p>
                        )}
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="pin">PIN (6 digits)</Label>
                        <div className="flex space-x-2">
                          <Input
                            id="pin"
                            type="password"
                            maxLength={6}
                            {...register("pin")}
                            data-testid="input-pin"
                          />
                          <Button
                            type="button"
                            variant="outline"
                            onClick={generateRandomPin}
                            data-testid="button-generate-pin"
                          >
                            Generate
                          </Button>
                        </div>
                        {errors.pin && (
                          <p className="text-sm text-red-600">{errors.pin.message}</p>
                        )}
                      </div>
                    </div>

                    <Button
                      type="submit"
                      disabled={createUserMutation.isPending}
                      data-testid="button-create-user"
                    >
                      {createUserMutation.isPending ? "Creating..." : "Create User"}
                    </Button>
                  </form>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>
      </main>
    </div>
  );
}

